Java
@PostMapping(&quot;/login&quot;)
public ModelAndView login(LoginData loginData) {
    if (LOGIN.equals(loginData.getLogin()) 
      &amp;&amp; PASSWORD.equals(loginData.getPassword())) {
        return new ModelAndView(&quot;success&quot;, 
